/**
 * Returns all photos for a specific album
 * @summary Get photos for an album
 */
export const getAlbumPhotos = (
  id: number,
  options?: SecondParameter<typeof axiosInstance>,
  signal?: AbortSignal,
) => {
  return axiosInstance<Photo[]>(
    {url: `/albums/${id}/photos`, method: 'GET', signal},
    options,
  )
}

export const getGetAlbumPhotosQueryKey = (id?: number) => {
  return [`/albums/${id}/photos`] as const
}

export const getGetAlbumPhotosQueryOptions = <
  TData = Awaited<ReturnType<typeof getAlbumPhotos>>,
  TError = ErrorType<unknown>,
>(
  id: number,
  options?: {
    query?: Partial<
      UseQueryOptions<Awaited<ReturnType<typeof getAlbumPhotos>>, TError, TData>
    >
    request?: SecondParameter<typeof axiosInstance>
  },
) => {
  const {query: queryOptions, request: requestOptions} = options ?? {}

  const queryKey = queryOptions?.queryKey ?? getGetAlbumPhotosQueryKey(id)

  const queryFn: QueryFunction<Awaited<ReturnType<typeof getAlbumPhotos>>> = ({
    signal,
  }) => getAlbumPhotos(id, requestOptions, signal)

  return {queryKey, queryFn, enabled: !!id, ...queryOptions} as UseQueryOptions<
    Awaited<ReturnType<typeof getAlbumPhotos>>,
    TError,
    TData
  > & {queryKey: DataTag<QueryKey, TData, TError>}
}

